import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import $ from "jquery";
import "../styles/note.css"

export default function Note(){
    let { uniqId } = useParams();
    
    const [note, setNote] = useState([]);
  
    async function getNote() {
      const response = await fetch(`/api/note/dear/${uniqId}`);
      const singleNote = await response.json();
  
      setNote(singleNote)
    }
    useEffect(() => {
      getNote();
    });

    return(
        <div className="container">
            <div class="envelope-container">
            <input id="flap" type="checkbox"/>
            <label class="flap" for="flap"></label>
            <div class="envelope-back"></div>
            <div class="card">
            <h1>Dear {note.recipient},</h1>
                        <p>{note.body}</p>
                        <h1>Sincerely, <br /> {note.sender}</h1>
            </div>
        </div>
    </div>
    )
}
