let recipientPreview = document.getElementById("recipientNamePreview");
let recipient = document.getElementById("formRecipient");


export default function Form(){
    const [noteRecipient, setCurrentRecipient] = useState({recipient:""})

    const changeState = (recipientName) => {  
    setCurrentRecipient({recipient:recipientName}); 
    }; 

    return(
        <div id="form-container">
            <div id="form">
                <img className="box" alt="A feather pen icon button" src={pen} id="pen-static"/><h1>Write your note</h1>
                <form>
                    <div className="form-group">
                        <label><h2>Recipient</h2></label> <br/>
                        <input id="formRecipient"type="text" className="form-control text-input" onInput={changeState(recipient.value)}/>
                    </div>
                    <div className="form-group">
                        <label><h2>Note body</h2></label> <br/>
                        <textarea className="form-control"  id="text-box"rows="5"></textarea>
                    </div>
                    <div className="form-group">
                        <label><h2>Sender</h2></label> <br/>
                        <input type="text" className="form-control text-input"/>
                    </div>
                    <div className="form-check">
                        <input type="checkbox" className="form-check-input" id="exampleCheck1"/>
                        <label className="form-check-label">Check me out</label>
                    </div>
                    <button type="submit" className="btn btn-primary">Submit</button>
                    </form>
            </div>
            <div id="note-preview">
                <input type="text" id="recipientNamePreview" readOnly/>
            </div>
        </div>
    )
}
